import { createApp } from 'vue'
import App from './App.vue'
import MouseSelection from "@/js/mouse_selection";

import $ from 'jquery'
window.$ = $

import api from './api/index.js'
window.api = api
window.Data = {}

import * as d3 from 'd3'
window.d3 = d3

import store from './store.js';
window.store = store


const app = createApp(App)
app.use(store)
app.use(MouseSelection);
console.log('MouseSelection 插件已加载');
app.mount('#app')
// 确保在 Vue 实例中使用 $mouseSelection
window.$mouseSelection = app.config.globalProperties.$mouseSelection;
