import { ref, onMounted, onUnmounted } from "vue";
import LoadingSpinner from "./LoadingSpinner.vue";
import ErrorDisplay from "./ErrorDisplay.vue";
import WikipediaContent from "./WikipediaContent.vue";
import { fetchWikipediaContent, injectWikipediaStyles } from "@/js/apiUtils";
import * as d3 from "d3";
import { renderChart } from "@/js/chartUtils";
export default {
  __name: 'MainComponent',
  props: {
    pageTitle: String,
    divId: String,
    selectContentClass: String
  },
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const props = __props;
    const pageHtml = ref("");
    const loading = ref(true);
    const error = ref("");
    const selectedText = ref({});
    const showPopup = ref({});
    const divRef = ref(null);
    const currentView = ref("text");
    const visualizationLoading = ref(false);
    const handleSelection = () => {
      const selection = window.getSelection();
      if (!selection.rangeCount) return;
      const range = selection.getRangeAt(0);
      const selectedHtml = range.cloneContents();
      const tempDiv = document.createElement("div");
      tempDiv.appendChild(selectedHtml);
      const table = tempDiv.querySelector("table");
      if (table) {
        table.classList.add("custom-table");
        selectedText.value[props.divId] = tempDiv.innerHTML;
      } else {
        const text = selection.toString().trim();
        if (!text) return;
        selectedText.value[props.divId] = text;
      }
      showPopup.value[props.divId] = true;
      currentView.value = "text";
      // 确保弹窗位置更新
      nextTick(() => {
        updatePopupPosition();
      });
    };
    const closePopup = () => {
      showPopup.value[props.divId] = false;
      selectedText.value[props.divId] = "";
      const container = props.divId === "div1" ? ".chart-container1" : ".chart-container2";
      d3.select(container).html("");
    };
    const switchView = async view => {
      currentView.value = view;
      if (view === "visual") {
        visualizationLoading.value = true;
        await processText();
        visualizationLoading.value = false;
      }
    };
    async function processText() {
      try {
        api.post("process_text", {
          text: selectedText.value[props.divId].toString().trim()
        },
        // 使用对应 divId 的选中内容
        data => {
          if (data.error) {
            console.error("后端返回的错误:", data.error);
            alert(`处理文章内容时出错: ${data.message}`);
            return;
          }
          const jsonData = data.json_data;
          console.log("后端返回的数据:", jsonData);

          // 根据 divId 选择容器
          const container = props.divId === "div1" ? ".chart-container1" : ".chart-container2";
          if (data.data_type === "Non-Visual") {
            renderNonVisualChart(container, data, {
              message: "当前数据无法可视化"
            });
          } else {
            renderChart(jsonData, data.chart_classification);
          }
        });
      } catch (error) {
        console.error("处理文章内容时出错:", error);
        alert("处理文章内容时出错，请稍后重试");
      }
    }

    // **更新遮罩层和弹出框的位置**
    const updatePopupPosition = () => {
      if (showPopup.value[props.divId] && divRef.value) {
        const popup = divRef.value.querySelector(".text-popup");
        const overlay = divRef.value.querySelector(".overlay");
        if (popup && overlay) {
          const rect = divRef.value.getBoundingClientRect();
          const scrollTop = divRef.value.scrollTop;
          const scrollLeft = divRef.value.scrollLeft;

          // 动态调整遮罩层和弹出框的位置
          overlay.style.top = `${scrollTop}px`;
          overlay.style.left = `${scrollLeft}px`;
          overlay.style.width = `${divRef.value.clientWidth}px`;
          overlay.style.height = `${divRef.value.clientHeight}px`;
          popup.style.top = `${rect.height / 2 + scrollTop}px`;
          popup.style.left = `${rect.width / 2 + scrollLeft}px`;
        }
      }
    };
    onMounted(() => {
      fetchWikipediaContent(props.pageTitle).then(html => {
        pageHtml.value = html;
        injectWikipediaStyles();
      }).catch(err => {
        error.value = err.message;
      }).finally(() => {
        loading.value = false;
      });
      if (divRef.value) {
        divRef.value.addEventListener("mouseup", handleSelection);
      }
      // 监听父容器滚动事件
      if (divRef.value) {
        divRef.value.addEventListener("scroll", updatePopupPosition);
      }
    });
    onUnmounted(() => {
      if (divRef.value) {
        divRef.value.removeEventListener("mouseup", handleSelection);
      }
    });
    const __returned__ = {
      props,
      pageHtml,
      loading,
      error,
      selectedText,
      showPopup,
      divRef,
      currentView,
      visualizationLoading,
      handleSelection,
      closePopup,
      switchView,
      processText,
      updatePopupPosition,
      ref,
      onMounted,
      onUnmounted,
      LoadingSpinner,
      ErrorDisplay,
      WikipediaContent,
      get fetchWikipediaContent() {
        return fetchWikipediaContent;
      },
      get injectWikipediaStyles() {
        return injectWikipediaStyles;
      },
      get d3() {
        return d3;
      },
      get renderChart() {
        return renderChart;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
