import { ref, onMounted, nextTick } from "vue";
import MouseSelection from "@/js/mouse_selection";
import bus from "@/js/eventBus.js"; // 事件总线

// 用于存储 HTML 内容

export default {
  __name: 'Div1',
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const div1Content = ref("");
    const div1Ref = ref(null); // 绑定 DOM

    onMounted(async () => {
      const src = "https://baike.baidu.com/item/%E5%94%90%E6%9C%9D/53699";
      await showHtml(src, div1Content);

      // 确保 v-html 渲染完成后再启动 MouseSelection
      nextTick(() => {
        if (div1Ref.value) {
          MouseSelection.start("Div1", div1Ref.value, selectContent => {
            if (selectContent) {
              console.log("✅ 选中的内容来自 Div1:", selectContent.content);
              bus.emit("div1Event", selectContent);
            }
          });
        }
      });
    });

    // 获取并展示内容
    async function showHtml(src, area) {
      try {
        api.get("html", {
          url: src
        }, data => {
          area.value = htmlToDom(data);
        });
      } catch (err) {
        console.log("❌ 请求失败：", err.message);
        area.value = ""; // 请求失败，返回空字符串
      }
    }

    // 将 HTML 内容转化为 DOM
    function htmlToDom(html) {
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, "text/html");
      return doc.documentElement.outerHTML;
    }
    const __returned__ = {
      div1Content,
      div1Ref,
      showHtml,
      htmlToDom,
      ref,
      onMounted,
      nextTick,
      get MouseSelection() {
        return MouseSelection;
      },
      get bus() {
        return bus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
