/**
 * 维基百科Infobox解析器
 * 提取结构化数据并规范化数值
 */
export function parseInfoboxForComparison(infoboxElement) {
  if (!infoboxElement) return {
    title: "",
    type: "",
    data: {
      sections: {}
    }
  };
  const result = {
    title: extractTitle(infoboxElement),
    type: detectInfoboxType(infoboxElement),
    data: {
      sections: {}
    }
  };
  let currentSection = "基本信息";
  const rows = infoboxElement.querySelectorAll('tr');
  rows.forEach(row => {
    // 处理section标题行
    if (row.querySelector('th[colspan="2"]')) {
      currentSection = cleanText(row.textContent) || currentSection;
      result.data.sections[currentSection] = {};
      return;
    }

    // 处理普通数据行
    const th = row.querySelector('th');
    const td = row.querySelector('td');
    if (!th || !td) return;
    const key = cleanFieldName(th.textContent);
    const value = extractValueFromText(cleanText(td.textContent));
    if (key && value !== null) {
      result.data.sections[currentSection][key] = value;
    }
  });
  return result;
}

/**
 * 从文本中提取规范化值
 */
function extractValueFromText(text) {
  if (!text) return null;

  // 1. 处理百分比 (6.8%)
  const percentMatch = text.match(/(\d+\.?\d*)%/);
  if (percentMatch) return parseFloat(percentMatch[1]);

  // 2. 处理货币值 ($4.19 trillion, ¥429,501)
  const currencyMatch = text.match(/(\$|¥|€|£)?\s*([\d,\.]+)\s*(万|亿|万亿|trillion|billion|million)?/i);
  if (currencyMatch) {
    let value = parseFloat(currencyMatch[2].replace(/,/g, ''));

    // 处理中文单位
    if (currencyMatch[3] === '万') value *= 1e4;
    if (currencyMatch[3] === '亿') value *= 1e8;
    if (currencyMatch[3] === '万亿') value *= 1e12;

    // 处理英文单位
    const unit = currencyMatch[3]?.toLowerCase();
    if (unit === 'million') value *= 1e6;
    if (unit === 'billion') value *= 1e9;
    if (unit === 'trillion') value *= 1e12;
    return value;
  }

  // 3. 处理纯数字 (123,456.78)
  const numberMatch = text.match(/^[\d,\.]+$/);
  if (numberMatch) return parseFloat(text.replace(/,/g, ''));

  // 4. 处理排名 (第5名)
  const rankMatch = text.match(/第\s*([零一二三四五六七八九十百千万\d]+)\s*名/);
  if (rankMatch) return chineseToNumber(rankMatch[1]);

  // 5. 默认返回文本
  return text;
}

/**
 * 中文数字转阿拉伯数字
 */
function chineseToNumber(chinese) {
  const map = {
    零: 0,
    一: 1,
    二: 2,
    三: 3,
    四: 4,
    五: 5,
    六: 6,
    七: 7,
    八: 8,
    九: 9,
    十: 10,
    百: 100,
    千: 1000,
    万: 10000
  };
  if (/^\d+$/.test(chinese)) return parseInt(chinese);
  let result = 0;
  let temp = 0;
  for (let i = 0; i < chinese.length; i++) {
    const char = chinese[i];
    if (map[char] < 10) {
      temp = map[char];
    } else {
      result += temp * map[char];
      temp = 0;
    }
  }
  return result + temp;
}

/**
 * 提取标题
 */
function extractTitle(element) {
  const sources = [() => element.querySelector('caption')?.textContent, () => element.querySelector('.infobox-title')?.textContent, () => element.querySelector('th[colspan="2"]')?.textContent];
  for (const source of sources) {
    const title = cleanText(source());
    if (title) return title;
  }
  return "无标题";
}

/**
 * 检测Infobox类型
 */
function detectInfoboxType(element) {
  const classText = `${element.className} ${element.textContent}`.toLowerCase();
  if (/country|nation|state|国家/.test(classText)) return "国家";
  if (/economy|经济/.test(classText)) return "经济";
  return "其他";
}

/**
 * 清理字段名
 */
function cleanFieldName(text) {
  return cleanText(text).replace(/[:：]$/, '').replace(/\s+/g, ' ').trim();
}

/**
 * 清理文本
 */
function cleanText(text) {
  return (text || "").replace(/\[\d+\]/g, '') // 去除引用标记[1]
  .replace(/\s+/g, ' ') // 合并空格
  .replace(/[[\]]/g, '') // 去除方括号
  .trim();
}

