import { ref, watch, onMounted, onUnmounted } from "vue";
import eventBus from "@/js/eventBus.js";
export default {
  __name: 'ArticleOutline',
  props: {
    content: String,
    // 文章 HTML
    divId: String // "div1" 或 "div3"
  },
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const props = __props;
    const isVisible = ref(false);
    const outline = ref([]);
    const buttonStyle = ref({});
    const outlineStyle = ref({});

    // **自定义关联数组**
    const linkedOutline = [{
      leftId: "heading-0-div1",
      rightId: "heading-1-div3"
    },
    // Early life vs High school career (两者时间线接近)
    {
      leftId: "heading-1-div1",
      rightId: "heading-2-div3"
    },
    // College career vs College career
    {
      leftId: "heading-4-div1",
      rightId: "heading-3-div3"
    },
    // Seattle SuperSonics / Oklahoma City Thunder vs Oklahoma City Thunder (时间段匹配)
    {
      leftId: "heading-5-div1",
      rightId: "heading-4-div3"
    },
    // Rookie of the Year vs 2012–2013: First All-Star and All-NBA selections (接近的时间段)
    {
      leftId: "heading-6-div1",
      rightId: "heading-5-div3"
    },
    // Breakthrough vs 2013–2014: First All-NBA First Team selection (同一时间段内的表现)
    {
      leftId: "heading-7-div1",
      rightId: "heading-6-div3"
    },
    // First NBA finals vs 2014–2016: Second in MVP voting and turnovers record (接近时间段)
    {
      leftId: "heading-8-div1",
      rightId: "heading-7-div3"
    },
    // 50–40–90 season vs 2016–2017: Point guard role and leading the NBA in assists
    {
      leftId: "heading-9-div1",
      rightId: "heading-8-div3"
    },
    // MVP season vs 2017–2018: MVP season and scoring title
    {
      leftId: "heading-10-div1",
      rightId: "heading-9-div3"
    },
    // Final seasons with the Thunder vs 2018–2019: Career high in scoring
    {
      leftId: "heading-11-div1",
      rightId: "heading-10-div3"
    },
    // Golden State Warriors vs Houston Rockets (时间段对比)
    {
      leftId: "heading-12-div1",
      rightId: "heading-11-div3"
    },
    // First championship and finals MVP vs 2019–2021: Third consecutive scoring title and trade request
    {
      leftId: "heading-13-div1",
      rightId: "heading-12-div3"
    },
    // Second championship and finals MVP vs Brooklyn Nets (2021–2022)
    {
      leftId: "heading-14-div1",
      rightId: "heading-13-div3"
    },
    // Three-peat chase and injury vs Philadelphia 76ers (2022–2023)
    {
      leftId: "heading-15-div1",
      rightId: "heading-14-div3"
    },
    // Brooklyn Nets (2019–2023) vs Los Angeles Clippers (2023–present)
    {
      leftId: "heading-16-div1",
      rightId: "heading-15-div3"
    },
    // Phoenix Suns (2023–present) vs Player profile (具体到球员的描述)
    {
      leftId: "heading-17-div1",
      rightId: "heading-16-div3"
    },
    // 55–40–90 season vs National team career
    {
      leftId: "heading-18-div1",
      rightId: "heading-17-div3"
    },
    // Top 10 in all-time scoring vs Career statistics
    {
      leftId: "heading-19-div1",
      rightId: "heading-18-div3"
    },
    // 30,000 points vs NBA Regular season
    {
      leftId: "heading-20-div1",
      rightId: "heading-19-div3"
    },
    // National team career vs Playoffs (类似的统计内容)
    {
      leftId: "heading-21-div1",
      rightId: "heading-20-div3"
    },
    // Player profile vs College (更侧重个人特色)
    {
      leftId: "heading-22-div1",
      rightId: "heading-21-div3"
    },
    // Personal life vs Personal life (相同内容)
    {
      leftId: "heading-23-div1",
      rightId: "heading-22-div3"
    },
    // Career statistics vs Politics (职业生涯与政治对比)
    {
      leftId: "heading-24-div1",
      rightId: "heading-23-div3"
    },
    // NBA vs See also (一些额外的参考链接)
    {
      leftId: "heading-25-div1",
      rightId: "heading-24-div3"
    },
    // Regular season vs References (赛季统计与外部链接)
    {
      leftId: "heading-26-div1",
      rightId: "heading-25-div3"
    },
    // Play-in vs External links (比赛详情与外部链接)
    {
      leftId: "heading-27-div1",
      rightId: "heading-26-div3"
    } // Playoffs vs Playoffs (附加内容对比)
    ];

    // **切换大纲的显示**
    const toggleOutline = () => {
      isVisible.value = !isVisible.value;
    };

    // **解析文章，提取 h1-h6 标题**
    const extractOutline = (content, containerId) => {
      if (!content) return [];
      const parser = new DOMParser();
      const doc = parser.parseFromString(content, "text/html");
      const headings = doc.querySelectorAll("h1, h2, h3, h4, h5, h6");
      return Array.from(headings).map((heading, index) => {
        const level = parseInt(heading.tagName.substring(1));
        const id = `heading-${index}-${containerId}`;
        heading.id = id;
        return {
          id,
          text: heading.textContent,
          level
        };
      });
    };

    // **监听 content 变化，更新大纲**
    watch(() => props.content, newContent => {
      outline.value = extractOutline(newContent, props.divId);

      // 提取另一个容器的大纲数据
      const otherContainerId = props.divId === "div1" ? "div3" : "div1";
      const otherContainer = document.getElementById(otherContainerId);
      if (otherContainer) {
        // 通过 eventBus 通知另一个大纲组件
        eventBus.emit("update-linked-outline", linkedOutline.value);
      }
    }, {
      immediate: true
    });

    // **监听 eventBus，确保两边大纲同步**
    eventBus.on("update-linked-outline", newLinkedOutline => {
      linkedOutline.value = newLinkedOutline;
    });

    // **检查是否存在关联章节**
    const isLinked = item => linkedOutline.value.some(linked => linked.leftId === item.id || linked.rightId === item.id);

    // **生成不同关联章节的边框颜色**
    const getBorderColor = id => {
      const linkedItem = linkedOutline.value.find(linked => linked.leftId === id || linked.rightId === id);
      if (linkedItem) {
        // 根据关联章节的索引生成不同的颜色
        const index = linkedOutline.value.indexOf(linkedItem);
        const colors = ["#FF6B6B", "#4ECDC4", "#FFD166", "#06D6A0", "#118AB2", "#073B4C"];
        return colors[index % colors.length];
      }
      return "transparent";
    };

    // **滚动到章节**
    const scrollToChapter = id => {
      // 跳转到当前文章的章节
      const element = document.getElementById(id);
      if (element) element.scrollIntoView({
        behavior: "smooth"
      });

      // 找到关联章节并跳转
      const linkedItem = linkedOutline.value.find(linked => linked.leftId === id || linked.rightId === id);
      if (linkedItem) {
        const targetId = linkedItem.leftId === id ? linkedItem.rightId : linkedItem.leftId;
        eventBus.emit("scroll-to-chapter", targetId);
      }
    };

    // **监听 eventBus，确保两个组件同步跳转**
    eventBus.on("scroll-to-chapter", targetId => {
      const targetElement = document.getElementById(targetId);
      if (targetElement) targetElement.scrollIntoView({
        behavior: "smooth"
      });
    });

    // **更新按钮和大纲的相对位置**
    const updatePosition = () => {
      const isDiv1 = props.divId === "div1";
      buttonStyle.value = {
        position: "absolute",
        top: "10px",
        [isDiv1 ? "right" : "left"]: "10px"
      };
      outlineStyle.value = {
        position: "absolute",
        top: "50px",
        [isDiv1 ? "right" : "left"]: "10px",
        width: "260px",
        maxHeight: "80vh",
        overflowY: "auto",
        zIndex: "1000",
        background: "white",
        borderRadius: "8px",
        boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
        padding: "15px"
      };
    };

    // **窗口滚动时更新大纲位置**
    const handleScroll = () => {
      const referenceElement = document.getElementById(props.divId);
      if (!referenceElement) return;
      const scrollY = referenceElement.scrollTop;
      buttonStyle.value.top = `${scrollY + 10}px`;
      outlineStyle.value.top = `${scrollY + 50}px`;
    };

    // **初始化**
    onMounted(() => {
      outline.value = extractOutline(props.content, props.divId);
      updatePosition();
      const referenceElement = document.getElementById(props.divId);
      if (referenceElement) referenceElement.addEventListener("scroll", handleScroll);
    });

    // **组件销毁时移除事件**
    onUnmounted(() => {
      const referenceElement = document.getElementById(props.divId);
      if (referenceElement) referenceElement.removeEventListener("scroll", handleScroll);
      eventBus.off("update-linked-outline");
      eventBus.off("scroll-to-chapter");
    });
    const __returned__ = {
      props,
      isVisible,
      outline,
      buttonStyle,
      outlineStyle,
      linkedOutline,
      toggleOutline,
      extractOutline,
      isLinked,
      getBorderColor,
      scrollToChapter,
      updatePosition,
      handleScroll,
      ref,
      watch,
      onMounted,
      onUnmounted,
      get eventBus() {
        return eventBus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
