import { createApp } from 'vue'
import App from './App.vue'
import * as echarts from 'echarts'
import VueECharts from 'vue-echarts'

// 注册 ECharts 组件
import { use } from 'echarts/core'
import { BarChart, LineChart, PieChart, TitleComponent, TooltipComponent, GridComponent, LegendComponent, ToolboxComponent, DatasetComponent } from 'echarts'

// 注册 ECharts 组件和组件库
use([BarChart, LineChart, PieChart, TitleComponent, TooltipComponent, GridComponent, LegendComponent, ToolboxComponent, DatasetComponent])

const app = createApp(App)

// 注册 VueECharts 组件
app.component('v-chart', VueECharts)

app.mount('#app')
