import { ref, onMounted, onUnmounted, nextTick } from "vue";
import MouseSelection from "@/js/mouse_selection"; // 引入自定义的鼠标选择工具
import bus from "@/js/eventBus.js"; // 事件总线（用于高亮传递）

export default {
  __name: 'WikipediaViewer',
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const pageTitle = ref("LeBron James"); // 页面标题
    const pageHtml = ref(""); // 页面HTML内容
    const loading = ref(true); // 加载状态
    const error = ref(""); // 错误信息

    // **获取 Wikipedia 页面内容**
    const fetchWikipediaContent = async () => {
      try {
        const apiUrl = `https://en.wikipedia.org/api/rest_v1/page/html/${encodeURIComponent(pageTitle.value)}`;
        const response = await fetch(apiUrl);
        if (!response.ok) throw new Error("获取数据失败");
        const html = await response.text();

        // 解析 HTML 并提取 <link rel="stylesheet">
        const parser = new DOMParser();
        const doc = parser.parseFromString(html, "text/html");

        // **动态注入 Wikipedia 样式**
        const styles = Array.from(doc.querySelectorAll('link[rel="stylesheet"]'));
        styles.forEach(link => {
          if (!document.querySelector(`link[href="${link.href}"]`)) {
            const styleTag = document.createElement("link");
            styleTag.rel = "stylesheet";
            styleTag.href = link.href;
            document.head.appendChild(styleTag);
          }
        });

        // 获取正文部分内容
        const contentElement = doc.querySelector("body");
        if (!contentElement) throw new Error("未找到内容");
        pageHtml.value = contentElement.innerHTML;

        // **手动引入 Wikipedia 官方 CSS**
        injectWikipediaStyles();
      } catch (err) {
        error.value = "获取 Wikipedia 内容失败: " + err.message;
      } finally {
        loading.value = false;
      }
    };

    // **手动注入 Wikipedia CSS**
    const injectWikipediaStyles = () => {
      const wikipediaStyles = ["https://en.wikipedia.org/w/load.php?modules=site.styles", "https://en.wikipedia.org/w/load.php?modules=ext.cite.styles", "https://en.wikipedia.org/w/load.php?modules=ext.scribunto.styles", "https://en.wikipedia.org/w/load.php?modules=skins.vector.styles"];
      wikipediaStyles.forEach(url => {
        if (!document.querySelector(`link[href="${url}"]`)) {
          const styleTag = document.createElement("link");
          styleTag.rel = "stylesheet";
          styleTag.href = url;
          document.head.appendChild(styleTag);
        }
      });
    };

    // **处理文本选择（提取选中的文本并传递）**
    const handleTextSelection = () => {
      const selection = window.getSelection().toString().trim();
      if (selection) {
        console.log("选中的内容:", selection);

        // 通过事件总线发出选中的内容
        bus.emit("div1Event", selection);
      }
    };

    // **组件挂载后加载 Wikipedia 内容**
    onMounted(() => {
      fetchWikipediaContent();
      nextTick(() => {
        // 初始化文本选择功能
        MouseSelection.start({
          rangeSelector: ".wikipedia-content",
          customId: "content"
        });
      });
    });

    // **组件卸载时解绑事件**
    onUnmounted(() => {
      // 解绑 mouseup 事件，防止内存泄漏
      document.querySelector(".wikipedia-content").removeEventListener("mouseup", handleTextSelection);
    });
    const __returned__ = {
      pageTitle,
      pageHtml,
      loading,
      error,
      fetchWikipediaContent,
      injectWikipediaStyles,
      handleTextSelection,
      ref,
      onMounted,
      onUnmounted,
      nextTick,
      get MouseSelection() {
        return MouseSelection;
      },
      get bus() {
        return bus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
