const HighlightText = {
  // 高亮选中的文字（支持跨节点）
  highlightSelection(highlightColor = 'yellow') {
    const selection = window.getSelection();
    if (!selection.isCollapsed) {
      const range = selection.getRangeAt(0);
      const startContainer = range.startContainer;
      const endContainer = range.endContainer;

      // 如果选中的开始和结束是同一个文本节点
      if (startContainer === endContainer && startContainer.nodeType === Node.TEXT_NODE) {
        this.highlightSingleNodeText(range, startContainer, highlightColor);
      } else {
        // 否则处理跨多个节点的选区
        this.highlightMultiNodeText(range, highlightColor);
      }

      selection.removeAllRanges(); // 清除选区
    }
  },

  // 高亮单一文本节点的选区
  highlightSingleNodeText(range, textNode, highlightColor) {
    const startOffset = range.startOffset;
    const endOffset = range.endOffset;

    // 确保起始和结束偏移量在合法范围内
    const validStartOffset = Math.min(startOffset, textNode.length);
    const validEndOffset = Math.min(endOffset, textNode.length);

    // 分割文本节点
    const highlightedText = textNode.splitText(validStartOffset); // 从起始偏移位置切分文本节点
    highlightedText.splitText(validEndOffset - validStartOffset); // 从结束偏移位置切分文本节点

    // 创建一个 span 来包裹选中的文本并应用高亮
    const span = document.createElement('span');
    span.style.backgroundColor = highlightColor; // 设置高亮样式
    const parent = textNode.parentNode;
    parent.replaceChild(span, textNode); // 替换原来的文本节点
    span.appendChild(textNode); // 把切分后的文本节点添加到 span 中
  },

  // 高亮跨多个节点的选区
  highlightMultiNodeText(range, highlightColor) {
    const startContainer = range.startContainer;
    const endContainer = range.endContainer;

    // 对选区内的所有节点进行遍历
    let node = startContainer;
    const nodesToHighlight = [];

    while (node) {
      // 只处理文本节点
      if (node.nodeType === Node.TEXT_NODE) {
        nodesToHighlight.push(node);
      }

      // 如果已经遍历到结束容器节点，停止
      if (node === endContainer) break;

      node = this.getNextNode(node);
    }

    // 对选中的文本节点应用高亮
    nodesToHighlight.forEach((textNode) => {
      const span = document.createElement('span');
      span.style.backgroundColor = highlightColor; // 设置高亮样式
      const parent = textNode.parentNode;
      parent.replaceChild(span, textNode); // 替换文本节点
      span.appendChild(textNode); // 将文本节点添加到 span 中
    });
  },

  // 获取下一个节点
  getNextNode(node) {
    if (node.firstChild) return node.firstChild;
    while (node) {
      if (node.nextSibling) return node.nextSibling;
      node = node.parentNode;
    }
    return null;
  }
};

export default HighlightText;
