    const textarea = ref(null); // 获取textarea的引用

    // 计算样式对象，支持传入的宽度和最大高度
    const textareaStyles = computed(() => ({
      width: props.width,
      // 根据传入的宽度设置
      maxHeight: props.height // 根据传入的最大高度设置
