import { ref, onMounted, nextTick } from "vue";
import MouseSelection from "@/js/mouse_selection";
import bus from "@/js/eventBus.js"; // 引入事件总线

// 用于存储 HTML 内容

export default {
  __name: 'Div1',
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const div1Content = ref("");

    // 页面初始化时加载内容
    onMounted(async () => {
      const src = "https://baike.baidu.com/item/%E5%94%90%E6%9C%9D/53699";
      await showHtml(src, div1Content);

      // 使用 nextTick 确保 v-html 渲染完成后再启动 MouseSelection
      nextTick(() => {
        MouseSelection.start({
          rangeSelector: ".selectContent1",
          // 传递范围选择器，支持向下检索
          customId: "div1" // 给这个区域设置自定义标识
        });
      });
    });

    // 绑定 mouseup 事件到特定 div
    document.querySelector(".selectContent1")?.addEventListener("mouseup", () => {
      const selectedContent = MouseSelection.selectionChangeFun(selectContent => {
        if (selectContent) {
          // 处理选中的内容
          bus.emit("div1Event", selectContent);
        } else {
          console.log("没有选择内容");
        }
      }, "div1"); // 传递 customId
    });

    // 获取并展示内容
    async function showHtml(src, area) {
      try {
        api.get("html", {
          url: src
        }, data => {
          area.value = htmlToDom(data);
        });
      } catch (err) {
        console.log("请求失败：", err.message);
        area.value = ""; // 如果请求失败，返回空字符串
      }
    }

    // 将 HTML 内容转化为 DOM
    function htmlToDom(html) {
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, "text/html");
      return doc.documentElement.outerHTML;
    }
    const __returned__ = {
      div1Content,
      showHtml,
      htmlToDom,
      ref,
      onMounted,
      nextTick,
      get MouseSelection() {
        return MouseSelection;
      },
      get bus() {
        return bus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
