import axios from 'axios';

const address = "http://localhost:8888/";

function apiAxios(type, url, params, callback) {
  const config = {
    method: type,
    url: address + url,
    headers: {
      'Content-Type': 'application/json'
    }
  };

  if (type === 'POST') {
    config.data = params;
  } else if (type === 'GET') {
    config.params = params;
  }

  axios(config)
    .then(response => {
      callback(response.data);
    })
    .catch(error => {
      console.error('There was an error for the request!', error);
    });
}

export default {
  get: function (url, params, callback) {
    return apiAxios('GET', url, params, callback);
  },
  post: function (url, params, callback) {
    return apiAxios('POST', url, params, callback);
  }
}
