const MouseSelection = {
  debounce(fn, time = 500) {
    let timeout = null;
    return function () {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        fn.apply(this, arguments);
      }, time);
    };
  },

  // 获取选中的内容，仅限于特定范围
  getSelectContent(rangeElement, customId = null) {
    const selection = window.getSelection();
    if (!selection || selection.isCollapsed) return null;

    const anchorNode = selection.anchorNode; // 获取选区的起始节点
    if (!anchorNode || !rangeElement.contains(anchorNode)) return null; // **限制选区范围**

    const range = selection.getRangeAt(0);
    const rangeClone = range.cloneContents();
    
    if (rangeClone.childElementCount > 0) {
      const container = document.createElement('div');
      container.appendChild(rangeClone);
      return {
        type: 'html',
        content: container.innerHTML,
        customId: customId
      };
    } else {
      return {
        type: 'text',
        content: selection.toString().trim(),
        customId: customId
      };
    }
  },

  // 处理选区变动后的回调
  selectionChangeFun(rangeElement, callback, customId) {
    const selectContent = MouseSelection.getSelectContent(rangeElement, customId);
    callback(selectContent);
    return selectContent;
  },

  // 启动选择功能，支持范围配置
  start(options = {}) {
    const { rangeSelector, customId } = options;
    const rangeElement = rangeSelector ? document.querySelector(rangeSelector) : document;

    if (!rangeElement) {
      console.error('Invalid range selector');
      return;
    }

    let selectionchangeMouseTrack = false;

    // 鼠标按下事件
    rangeElement.addEventListener('mousedown', () => {
      selectionchangeMouseTrack = true;
    });

    // 鼠标抬起事件
    rangeElement.addEventListener('mouseup', MouseSelection.debounce(() => {
      MouseSelection.selectionChangeFun(rangeElement, (selectContent) => {
        if (selectContent) {
          console.log(`选中的内容来自 ${customId}:`, selectContent);
        } else {
          console.log("没有选择内容");
        }
      }, customId);
    }, 100));

    // 选择区域发生变化时
    rangeElement.addEventListener('selectionchange', MouseSelection.debounce(() => {
      if (selectionchangeMouseTrack) return;
      MouseSelection.selectionChangeFun(rangeElement, (selectContent) => {
        if (!selectContent) console.log("没有选择内容");
      }, customId);
    }));
  }
};

export default MouseSelection;
