import { ref, onMounted } from "vue";
import { PageHTML } from "wikimedia-page-library"; // 导入 PageHTML

export default {
  __name: 'WikipediaViewer',
  setup(__props, _ref) {
    let {
      expose: __expose
    } = _ref;
    __expose();
    const pageTitle = ref("LeBron James"); // 页面标题
    const loading = ref(true); // 加载状态
    const error = ref(""); // 错误信息

    // 获取维基百科内容
    const fetchWikipediaContent = async () => {
      try {
        const apiUrl = `https://en.wikipedia.org/api/rest_v1/page/html/${encodeURIComponent(pageTitle.value)}`;
        const response = await fetch(apiUrl);
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const html = await response.text();

        // 使用 PageHTML 渲染内容
        const pageHTML = new PageHTML(html);
        pageHTML.render(document.querySelector(".wikipedia-content"));
      } catch (err) {
        error.value = "Error fetching Wikipedia content: " + err.message;
      } finally {
        loading.value = false; // 结束加载状态
      }
    };

    // 在组件挂载时调用 API
    onMounted(() => {
      fetchWikipediaContent();
    });
    const __returned__ = {
      pageTitle,
      loading,
      error,
      fetchWikipediaContent,
      ref,
      onMounted,
      get PageHTML() {
        return PageHTML;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
