const MouseSelection = {
  debounce(fn, time = 500) {
    let timeout = null;
    return function () {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        fn.apply(this, arguments);
      }, time);
    };
  },

  // 获取选中的内容
  getSelectContent(customId = null) {
    const selection = window.getSelection();
    if (!selection || selection.isCollapsed) return null;

    const range = selection.getRangeAt(0);
    const rangeClone = range.cloneContents();
    const textContent = selection.toString().trim();

    if (rangeClone.childElementCount > 0) {
      const container = document.createElement('div');
      container.appendChild(rangeClone);
      return {
        type: 'html',
        content: container.innerHTML,
        customId: customId
      };
    } else if (textContent.length) {
      return {
        type: 'text',
        content: textContent,
        customId: customId
      };
    }
    return null;
  },

  // 处理选区变动后的回调
  selectionChangeFun(callback, customId) {
    const selectContent = MouseSelection.getSelectContent(customId);
    callback(selectContent);
    return selectContent;
  },

  // 启动鼠标选中监听
  start({ rangeSelector, customId }) {
    const rangeElement = document.querySelector(rangeSelector) || document;

    if (!rangeElement) {
      console.error('Invalid range selector');
      return;
    }

    let selectionActive = false;

    rangeElement.addEventListener('mousedown', () => {
      selectionActive = true;
    });

    rangeElement.addEventListener('mouseup', MouseSelection.debounce(() => {
      selectionActive = false;
      MouseSelection.selectionChangeFun((selectContent) => {
        if (selectContent) {
          console.log(`✅ 选中的内容来自 ${customId}:`, selectContent);
        } else {
          console.log("❌ 没有选择内容");
        }
      }, customId);
    }, 100));

    rangeElement.addEventListener('selectionchange', MouseSelection.debounce(() => {
      if (selectionActive) return;
      MouseSelection.selectionChangeFun((selectContent) => {
        if (selectContent) {
          console.log(`✅ selectionchange 触发: ${customId}:`, selectContent);
        }
      }, customId);
    }, 200));
  }
};

export default MouseSelection;
